import React, { useState } from 'react';
import './App.css'; // Assuming you have an App.css

function App() {
  const [query, setQuery] = useState('');
  const [url, setUrl] = useState('');
  const [summary, setSummary] = useState('');
  const [extractedData, setExtractedData] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:8000'; // Matches your backend port

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setSummary('');
    setExtractedData('');

    try {
      const response = await fetch(`${API_URL}/scrape_and_summarize/`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ user_query: query, url: url }),
      });

      const data = await response.json();

      if (data.success) {
        setSummary(data.summary);
        setExtractedData(data.extracted_data);
      } else {
        setError(data.error || 'An unknown error occurred.');
      }
    } catch (err) {
      setError('Failed to connect to the backend API.');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Web Scraping Agent UI</h1>
        <form onSubmit={handleSubmit}>
          <div>
            <label htmlFor="url-input">Website URL:</label>
            <input
              id="url-input"
              type="text"
              value={url}
              onChange={(e) => setUrl(e.target.value)}
              placeholder="e.g., https://example.com"
              required
            />
          </div>
          <div>
            <label htmlFor="query-input">Your Query:</label>
            <textarea
              id="query-input"
              value={query}
              onChange={(e) => setQuery(e.target.value)}
              placeholder="e.g., Summarize the main points of this article."
              rows="4"
              required
            />
          </div>
          <button type="submit" disabled={loading}>
            {loading ? 'Processing...' : 'Scrape & Summarize'}
          </button>
        </form>

        {error && <p className="error-message">Error: {error}</p>}

        {summary && (
          <div className="results">
            <h2>Summary:</h2>
            <p>{summary}</p>
            <h2>Extracted Data:</h2>
            <pre>{extractedData}</pre> {/* Display raw data for debugging */}
          </div>
        )}
      </header>
    </div>
  );
}

export default App;
